INSERT INTO `comment` (`create_time`, `pd_sid`, `client_sid`, `c_stars`, `content`, `pic_url`) VALUES
('2020-11-01 20:50:28', 1, 1, 4, '舒適度跟音質超好，\r\n消噪耳罩耳機這款真的非常值得入手。', NULL),
('2020-10-14 20:50:42', 1, 2, 5, '音質比我想像中好很多，外觀也很潮很好看！推薦！', NULL),
('2020-11-01 21:42:59', 1, 2, 5, '買來送給男朋友，他非常喜歡！價錢合理音質又棒！大推！', 'pd1.jpg,pd1-2.jpg'),
('2020-11-01 21:48:51', 1, 3, 3, '雖然很漂亮，但覺得對音質的期望太大，覺得價錢沒有符合期待', 'pd001.jpg,pd001-2.jpg'),
('2020-10-14 20:50:42', 2, 1, 4, '高頻不刺耳、中頻順耳就是低頻有點糊，整體來說很棒!', NULL),
('2020-11-16 17:08:51', 1, 1, 5, 'good', NULL),
('2020-11-16 17:08:55', 2, 1, 4, 'sssssss', NULL),
('2020-11-16 17:08:59', 3, 1, 3, 'ffffff', NULL),
('2020-11-16 17:10:51', 1, 1, 5, 'good', NULL),
('2020-11-16 17:10:59', 3, 1, 3, 'ffffff', NULL);
